import React, { useState } from "react";
import {
  SafeAreaView,
  StyleSheet,
  Alert,
  Platform,
  Keyboard,
  Dimensions,
  Animated,
  KeyboardAvoidingView,
  Easing,
} from "react-native";
import { View, Text, CheckBox, Button } from "native-base";
import { useHeaderHeight } from "@react-navigation/stack";
import AnimatedEllipsis from "react-native-animated-ellipsis";
import { Colors, IconButton } from "react-native-paper";

import Inputs from "./Input";
import Ketqua from "./Ketqua";

const screen_height = Dimensions.get("window").height;
const screen_width = Dimensions.get("window").width;

export default function ComboBox() {
  const headerHeight = useHeaderHeight();

  //* State :
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [checkboxValue, setCheckboxValue] = useState([
    {
      label: "Mã hồ sơ tuyển sinh",
      value1: "",
      type: "1",
      checked: false,
    },
    { label: "Mã học sinh", value1: "", value2: "", type: "2", checked: false },
    {
      label: "Số báo danh (Nếu có)",
      value1: "",
      type: "3",
      checked: false,
    },
  ]);

  const position = useState(new Animated.Value(0))[0];
  const Appear = () => {
    Animated.timing(position, {
      toValue: -screen_height,
      duration: 1000,
      easing: Easing.ease,
      useNativeDriver: true,
    }).start();
  };
  const Disapear = () => {
    Animated.timing(position, {
      toValue: 0,
      duration: 1000,
      easing: Easing.ease,
      useNativeDriver: true,
    }).start();
  };
  //#region API
  //* Lấy API
  const getApi = async (type, value1, value2) => {
    setLoading(true);
    let namhoc = "";
    let mahoso = "",
      mahocsinh = "",
      matkhau = "",
      sbd = "";
    switch (type) {
      case "1":
        mahoso = value1;
        break;
      case "2":
        mahocsinh = value1;
        matkhau = value2;
        break;
      case "3":
        sbd = value1;
        break;
      default:
        break;
    }
    await fetch(
      `http://tuyensinhvinhphuc.eduvi.vn/api/TSAPIService/tracuuketqua?type=${type}&mahoso=${mahoso}&mahocsinh=${mahocsinh}&matkhau=${matkhau}&sobaodanh=${sbd}&namhoc=${namhoc}`
    )
      .then((response) => response.json())
      .then((responseJson) => {
        let result = responseJson.Result.data;
        //console.log(result);
        result.ThongTinHoSo == null
          ? (Alert.alert(
              "Không tồn tại kết quả tra cứu ! Vui lòng kiểm tra lại thông tin đã nhập "
            ),
            setData(null))
          : (setData(result), Appear());
      })
      .catch(() => {
        Alert.alert(
          "Không tồn tại kết quả tra cứu ! Vui lòng kiểm tra lại thông tin đã nhập "
        );
        setData(null);
      });
    setLoading(false);
  };
  //#endregion

  //#region Xử lý điều kiện ô nhập
  //* Cập nhật value
  const updateValue = (value, valueIndex) => {
    const newValue = checkboxValue.map((checkbox, i) => {
      return checkbox.checked
        ? valueIndex == 1
          ? {
              ...checkbox,
              value1: value,
            }
          : {
              ...checkbox,
              value2: value,
            }
        : {
            ...checkbox,
          };
    });
    setCheckboxValue(newValue);
  };
  //* Chỉ cho check 1
  const checkboxHandler = (value, index) => {
    const newValue = checkboxValue.map((checkbox, i) => {
      if (i !== index)
        return {
          ...checkbox,
          checked: false,
        };
      if (i === index) {
        const item = {
          ...checkbox,
          checked: !checkbox.checked,
        };
        return item;
      }
      return checkbox;
    });
    setCheckboxValue(newValue);
  };
  //TODO Kiểm tra lại điều kiện chỗ checkboxValue[1] tại sao chỉ check null của ô 1
  //* Kiểm tra value tại ô đó có rỗng không
  const EmptyOrNot = (i) => {
    return i == 0 || 2
      ? checkboxValue[i].value1 == ""
        ? true
        : false
      : checkboxValue[i].value1 == "" || checkboxValue[i].value2 == ""
      ? true
      : false;
  };
  //#endregion

  //* Tra cứu
  const Tracuu = () => {
    setData(null);
    for (var i = 0; i < checkboxValue.length; i++) {
      if (checkboxValue[i].checked) {
        if (EmptyOrNot(i)) {
          return Alert.alert("Mời bạn nhập đầy đủ thông tin trước khi tra cứu");
        }
        return getApi(
          checkboxValue[i].type,
          checkboxValue[i].value1,
          checkboxValue[i].value2
        );
      }
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      {loading && (
        <View style={{ position: "absolute", top: 5 }}>
          <AnimatedEllipsis
            numberOfDots={3}
            minOpacity={0.4}
            animationDelay={200}
            style={{
              color: "#61b15a",
              fontSize: 100,
              letterSpacing: -15,
            }}
          />
        </View>
      )}
      <Animated.View
        style={[
          styles.main,
          {
            transform: [
              {
                translateY: position,
              },
            ],
          },
        ]}
      >
        <KeyboardAvoidingView
          behavior={Platform.OS === "ios" ? "padding" : "height"}
          style={styles.top}
        >
          <View style={[styles.block, { marginBottom: headerHeight }]}>
            <View style={styles.checkBoxContainer}>
              {/* Checkbox */}
              {checkboxValue.map((checkbox, i) => (
                <View key={i} style={styles.perCheckContainer}>
                  <CheckBox
                    style={styles.checkbox}
                    checked={checkbox.checked}
                    color={checkbox.checked ? "#61b15a" : "#008577"}
                    onPress={(value) => checkboxHandler(value, i)}
                  />
                  <Text style={styles.label}>{"" + checkbox.label + ""}</Text>
                </View>
              ))}
            </View>
            {/* Input */}
            <View style={styles.inputContainer}>
              <Inputs checkboxValue={checkboxValue} updateValue={updateValue} />
            </View>
            {/* Button */}
            <View
              style={[
                styles.inputContainer,
                { borderSize: 0, borderColor: "" },
              ]}
            >
              {checkboxValue.some((item) => item.checked) && (
                <Button
                  success
                  style={styles.button}
                  onPress={() => {
                    Keyboard.dismiss();
                    Tracuu();
                  }}
                >
                  <Text>Tra cứu</Text>
                </Button>
              )}
            </View>
          </View>
        </KeyboardAvoidingView>
        <View style={styles.bottom}>
          <IconButton
            icon="home-search"
            color="#FFF"
            size={32}
            style={{
              position: "absolute",
              bottom: headerHeight + 10,
              right: 10,
              backgroundColor: Colors.red500,
              zIndex: 100,
            }}
            onPress={Disapear}
          />
          {data !== null && <Ketqua data={data} bottom={headerHeight} />}
        </View>
      </Animated.View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  main: {
    width: screen_width * 2,
    height: screen_height * 2,
  },
  top: {
    width: screen_width,
    height: screen_height,

    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#DEEBFE",
  },
  bottom: {
    width: screen_width,
    height: screen_height,
    backgroundColor: "#DEEBFE",
  },
  result: {
    flex: 1,
  },
  block: {
    width: "90%",
    backgroundColor: "white",
    alignItems: "center",
    //* bóng mờ
    borderRadius: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 5,
    },
    shadowOpacity: 0.34,
    shadowRadius: 6.27,

    elevation: 10,
  },
  checkBoxContainer: {
    alignItems: "stretch",
    marginTop: "7%",
    width: "80%",
  },
  perCheckContainer: {
    backgroundColor: "#FFFFFF",
    borderRadius: 10,

    alignItems: "stretch",
    flexDirection: "row",
    marginBottom: 10,

    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 5,
    },
    shadowOpacity: 0.34,
    shadowRadius: 6.27,

    elevation: 10,
  },
  inputContainer: {
    alignItems: "stretch",
    width: "90%",
    margin: "2%",
  },
  checkbox: {
    margin: "4%",
  },
  label: {
    fontSize: 18,
    padding: 12,
  },
  button: {
    marginBottom: "5%",
    alignSelf: "center",
    borderRadius: 25,
    textShadowColor: "#bbbbbb",

    shadowColor: "rgba(0, 0, 0, 0.1)",
    shadowOpacity: 0.8,
    elevation: 6,
    shadowRadius: 15,
    shadowOffset: { width: 1, height: 13 },
  },
});
