import React, { useEffect, useState, useLayoutEffect } from "react";
import {
  KeyboardAvoidingView,
  Modal,
  Platform,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  TextInput,
  TouchableOpacity,
} from "react-native";
import { View, Text, Button, Picker, CheckBox } from "native-base";

import { BlurView } from "expo-blur";
import DateTimePicker from "@react-native-community/datetimepicker";
import RadioButtonRN from "radio-buttons-react-native";
import { Colors, IconButton } from "react-native-paper";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from "react-native-table-component";
import FlashMessage from "react-native-flash-message";
import { showMessage, hideMessage } from "react-native-flash-message";
import { LogBox } from "react-native";
LogBox.ignoreLogs([
  "VirtualizedLists should never be nested inside plain ScrollViews with the same orientation - use another VirtualizedList-backed container instead.",
]);
//* Hàm xử lý picker ngày tháng
function useInput() {
  const [date, setDate] = useState(new Date());
  const [mode, setMode] = useState("date");
  const [show, setShow] = useState(false);

  const showMode = (currentMode) => {
    setShow(true);
    setMode(currentMode);
  };
  const showDatepicker = () => {
    showMode("date");
  };

  const onChange = (event, selectedDate) => {
    const currentDate = selectedDate || date;
    setShow(Platform.OS === "ios");
    setDate(currentDate);
  };
  return {
    date,
    showDatepicker,
    show,
    mode,
    onChange,
  };
}
//* hàm chuyển đổi ngày tháng
const date = require("s-date");
export default function Trangdangky({ route, navigation }) {
  const { DoiTuongTuyenSinh, IDKyThi } = route.params;
  useLayoutEffect(() => {
    navigation.setOptions({
      title: "Đăng ký tuyển sinh",
    });
  });
  //#region DatePicker
  const inputMe = useInput(new Date());
  const inputCon = useInput(new Date());
  const inputCha = useInput(new Date());
  const inputNGH = useInput(new Date());
  //#endregion

  const [data, setData] = useState({
    MaHocSinh: "",
    MatKhau: "",
    HoTen: "",
    NgaySinh: "",
    DanToc: "",
    GioiTinh: false,
    // Nơi sinh
    IDTinhNS: "",
    IDHuyenNS: "",
    IDXaNS: "",
    DiaChiNS: "",
    // Thường trú
    IDTinhTT: "",
    IDHuyenTT: "",
    IDXaTT: "",
    DiaChiTT: "",
    // Tạm trú
    IDTinhTamTru: "",
    IDHuyenTamTru: "",
    IDXaTamTru: "",
    DiaChiTamTru: "",
    // Nơi ở
    IDTinh: "",
    IDHuyen: "",
    IDXa: "",
    DiaChi: "",
    // Chương trình cấp dưới
    IDTinhCT: "",
    IDHuyenCT: "",
    IDXaCT: "",
    TenLopCT: "",

    HanhKiem: "",
    HocLuc: "",
    HocBa: [],
    NguyenVong: [
      {
        NguyenVong: 1,
        IDTruong: "",
        CoSoDangKy: "",
        IDLopChuyen: "",
        IDMonChuyen: "",
        lstLopChuyen: [],
      },
    ],
    DoiTuongUuTien: [],
    CoGiaiThuongQuocGia: false,
    DanhSachFileDinhKem: [],

    HoTenMe: "",
    NgaySinhMe: "",
    CMNDMe: "",
    NgheNghiepMe: "",
    SDTMe: "",

    HoTenCha: "",
    NgaySinhCha: "",
    CMNDCha: "",
    NgheNgiepCha: "",
    SDTCha: "",

    HoTenNguoiGiamHo: "",
    NgaySinhNguoiGiamHo: "",
    CMNDNguoiGiamHo: "",
    NgheNghiep: "",
    SDTNGH: "",

    DienThoaiLienHe: "",
    MailLienHe: "",
    Xacnhanthongtin: false,
    NguyenVong_Picker: [
      // Nguyện vọng 1
      [
        {
          ID: 1,
          IDTruong: "",
          MaTruong: "",
          TenTruong: "Chọn trường",
          DiaChi: "",
          IDTinh: "",
          IDQuan: "",
          IDPhuong: "",
          idKeHoach: "",
        },
      ],
    ],
  });
  //console.log(data.NguyenVong);

  //#region Học bạ: Table - Call API
  //* Tạo bảng
  const inputTable = (indexRow, indexCell, value) => (
    <TextInput
      style={{ paddingLeft: 5 }}
      placeholder="Nhập điểm ..."
      keyboardType={"number-pad"}
      multiline={false}
      onChangeText={(value) => NhapDiemHocBa(indexRow, indexCell, value)}
    />
  );
  const [table, setTable] = useState({
    tableHead: [],
    tableTitle: [],
    //tableData: [],
  });
  const NhapDiemHocBa = (indexRow, indexCell, value) => {
    const arr = [...data.HocBa];
    arr[indexRow][indexCell].Diem = value;
    setData((prev) => ({
      ...prev,
      HocBa: arr,
    }));
  };
  //* Gọi API danh sách học bạ
  useEffect(() => {
    fetch(
      `http://tuyensinhvinhphuc.eduvi.vn/api/TSAPIService/gethocba?idKyThi=${IDKyThi}`
    )
      .then((response) => response.json())
      .then((responseJson) => {
        const diemData = [];
        const lstMon = [];
        const lstLop = [];
        //const lstDiem = [];
        // Đổ dữ liệu lớp và tạo điểm
        responseJson.Result.data.lstLopHoc.map((item_Lop, index_Lop) => {
          diemData.push([]);
          //lstDiem.push([]);
          lstLop.push(item_Lop);
          responseJson.Result.data.lstMonHoc.map((item_Mon, index_Mon) => {
            //lstDiem[index_Lop].push(inputTable());
            // Tạo đối tượng điểm data
            const obj = {
              IDMonThi: item_Mon.ID,
              Lop: item_Lop,
              Diem: null,
            };
            diemData[index_Lop].push(obj);
          });
        });
        // Đổ dữ liệu môn
        responseJson.Result.data.lstMonHoc.map((item_Mon, index_Mon) => {
          lstMon.push(item_Mon.Ten);
        });

        setData((prevState) => ({
          ...prevState,
          HocBa: diemData,
        }));
        setTable((prevState) => ({
          ...prevState,
          tableHead: lstMon,
          tableTitle: lstLop,
          //tableData: lstDiem,
        }));
      });
  }, [0]);

  //#endregion

  //#region DropPicker: Dữ liệu - Thay đổi value khi chọn
  //* Dữ liệu trong dropDown
  const dantocData = require("../Dangkytuyensinh/Dantoc.json");
  const [picker, setPicker] = useState({
    DanToc: dantocData.dantoc,
    // Nơi sinh
    IDTinhNS: [
      {
        id: "",
        name: "Chọn Tỉnh/Thành phố",
      },
    ],
    IDHuyenNS: [
      {
        id: "",
        name: "Chọn Quận/Huyện",
      },
    ],
    IDXaNS: [
      {
        id: "",
        name: "Chọn Phường/Xã",
      },
    ],
    // Thường trú
    IDTinhTT: [
      {
        id: "",
        name: "Chọn Tỉnh/Thành phố",
      },
    ],
    IDHuyenTT: [
      {
        id: "",
        name: "Chọn Quận/Huyện",
      },
    ],
    IDXaTT: [
      {
        id: "",
        name: "Chọn Phường/Xã",
      },
    ],
    // Tạm trú
    IDTinhTamTru: [
      {
        id: "",
        name: "Chọn Tỉnh/Thành phố",
      },
    ],
    IDHuyenTamTru: [
      {
        id: "",
        name: "Chọn Quận/Huyện",
      },
    ],
    IDXaTamTru: [
      {
        id: "",
        name: "Chọn Phường/Xã",
      },
    ],
    // Nơi ở
    IDTinh: [
      {
        id: "",
        name: "Chọn Tỉnh/Thành phố",
      },
    ],
    IDHuyen: [
      {
        id: "",
        name: "Chọn Quận/Huyện",
      },
    ],
    IDXa: [
      {
        id: "",
        name: "Chọn Phường/Xã",
      },
    ],
    // Chương trình cấp dưới
    IDTinhCT: [
      {
        id: "",
        name: "Chọn Tỉnh/Thành phố",
      },
    ],
    IDHuyenCT: [
      {
        id: "",
        name: "Chọn Quận/Huyện",
      },
    ],
    IDXaCT: [
      {
        id: "",
        name: "Chọn Phường/Xã",
      },
    ],
    DoiTuongUuTien: [],
    HanhKiem: [
      {
        id: "",
        name: "Chọn hạnh kiểm",
      },
      {
        id: "Tốt",
        name: "Tốt",
      },
      {
        id: "Khá",
        name: "Khá",
      },
      {
        id: "Trung bình",
        name: "Trung bình",
      },
      {
        id: "Yếu",
        name: "Yếu",
      },
    ],
    HocLuc: [
      {
        id: "",
        name: "Chọn học lực",
      },
      {
        id: "Giỏi",
        name: "Giỏi",
      },
      {
        id: "Khá",
        name: "Khá",
      },
      {
        id: "Trung bình",
        name: "Trung bình",
      },
      {
        id: "Yếu",
        name: "Yếu",
      },
      {
        id: "Kém",
        name: "Kém",
      },
    ],
  });
  /*   console.log(`data.NguyenVong: ${data.NguyenVong.length}
  data.Picker: ${data.NguyenVong_Picker.length}`); */
  const [DSdoituonguutien, setDSdoituonguutien] = useState([]);
  //* Chọn giá trị cho Picker
  const changeValuePicker = (arg) => {
    setData((prevState) => ({
      ...prevState,
      ...arg,
    }));
  };
  //#endregion

  //#region Ảnh: Thêm - Xóa
  // //* Dữ liệu ảnh
  // const [pickerResult, setPickerResult] = useState(null);
  // //* Nén ảnh thành base64
  // const _pickImg = async () => {
  //   let pickerResult = await ImagePicker.launchImageLibraryAsync({
  //     base64: true,
  //     allowsEditing: false,
  //     aspect: [4, 3],
  //   });

  //   setPickerResult({
  //     pickerResult,
  //   });
  // };
  // let imageUri = pickerResult
